html:pre.html:code:
void echanger (int x, int y){
  int z = x;
  x = y;
  y = z;
}

int main (){
  int a = 0;
  int b = 1;
  echanger (a,b);
  // a ce point du programme,
  // avec un appel par valeur,
  // a = 0, b = 1
  // avec un appel par référence,
  // a = 1, b = 0
  return 0;
}

void echanger (int x, int y){
  int z = x;
  x = y;
  y = z;
}

int main (){
  int a = 0;
  int b = 1;
  echanger (a,b);
  // a ce point du programme,
  // a = 0, b = 1
  return 0;
}

void echanger (int &x, int &y){
  int z = x;
  x = y;
  y = z;
}

int main (){
  int a = 0;
  int b = 1;
  echanger (a,b);
  // a ce point du programme,
  // a = 1, b = 0
  return 0;
}

void echangerPremiers (int x[]){
  int y = x[0];
  x[0] = x[1];
  x[1] = y;
}

int main (){
  int a[2] = {0,1};
  echangerPremiers (a);
  // a ce point du programme,
  // a = {1,0}
  return 0;
}

