html:pre.html:code:
void echangerPremiers (int x[]){
  int y = x[0];
  x[0] = x[1];
  x[1] = y;
}

int main (){
  int a[2] = {0,1};
  echangerPremiers (a);
  // a ce point du programme,
  // a = {1,0}
  return 0;
}

